package cn.test.demo.base.mvp;import android.util.Log;import androidx.lifecycle.Lifecycle;import androidx.lifecycle.LifecycleObserver;import androidx.lifecycle.LifecycleOwner;import androidx.lifecycle.OnLifecycleEvent;/** * Created by Sam on  2021-12-06  22:13 * Describe: */public class BaseModel implements IModel, LifecycleObserver {    protected final String TAG = this.getClass().getSimpleName();    @Override    public void onDestroy() {    }    @OnLifecycleEvent(Lifecycle.Event.ON_DESTROY)    void onDestroy(LifecycleOwner owner) {        Log.e(TAG, "onDestroy: LifecycleOwner");        owner.getLifecycle().removeObserver(this);    }}