package cn.test.demo.http.repository;import java.util.Map;import cn.test.demo.base.mvp.JsonUtil;import cn.test.demo.http.retrofit.ApiClient;import cn.test.demo.util.SwitchSchedulers;import io.reactivex.Flowable;/** * Created by Sam on  2021-12-06  23:19 * Describe: */public class HttpDataRepository {    public static <T> Flowable httpGet(String pullUrl, Map<String, String> headers, final Class<T> clazz) {        return ApiClient.getInstance()                .getApiDataService()                .httpGet(pullUrl, headers)                .compose(SwitchSchedulers.rxSchedulerTransformer())                .map(responseBody -> JsonUtil.Str2JsonBean(responseBody.string(), clazz));    }    public static <T> Flowable httpPost(String pullUrl, Map<String, String> params, Map<String, String> headers, final Class<T> clazz) {        return ApiClient.getInstance()                .getApiDataService()                .httpPost(pullUrl, params, headers)                .compose(SwitchSchedulers.rxSchedulerTransformer())                .map(responseBody -> JsonUtil.Str2JsonBean(responseBody.string(), clazz));    }}