package cn.test.demo.http.retrofit;import java.util.Map;import io.reactivex.Flowable;import okhttp3.RequestBody;import okhttp3.ResponseBody;import retrofit2.http.Body;import retrofit2.http.DELETE;import retrofit2.http.FieldMap;import retrofit2.http.FormUrlEncoded;import retrofit2.http.GET;import retrofit2.http.HeaderMap;import retrofit2.http.PATCH;import retrofit2.http.POST;import retrofit2.http.PUT;import retrofit2.http.Url;/** * Created by Sam on  2021-12-06  17:16 * Describe: */public interface ApiHttpRequestService {    @GET    Flowable<ResponseBody> httpGet(@Url String fullUrl, @HeaderMap Map<String, String> headers);    @FormUrlEncoded    @POST    Flowable<ResponseBody> httpPost(@Url String fullUrl, @FieldMap Map<String, String> fields, @HeaderMap Map<String, String> headers);    @DELETE    Flowable<ResponseBody> httpDelete(@Url String fullUrl, @HeaderMap Map<String, String> headers);    @FormUrlEncoded    @PUT    Flowable<ResponseBody> httpDelete(@Url String fullUrl, @FieldMap Map<String, String> fields, @HeaderMap Map<String, String> headers);    @FormUrlEncoded    @PATCH    Flowable<ResponseBody> httpPatch(@Url String fullUrl, @FieldMap Map<String, String> fields, @HeaderMap Map<String, String> headers);    @POST    Flowable<ResponseBody> httpPostJson(@Url String fullUrl, @Body RequestBody body, @HeaderMap Map<String, String> headers);    @PUT    Flowable<ResponseBody> httpPut(@Url String fullUrl, @Body RequestBody body, @HeaderMap Map<String, String> headers);    @POST    Flowable<ResponseBody> userLogin(@Url String fullUrl, @Body RequestBody body, @HeaderMap Map<String, String> headers);    @PUT    Flowable<ResponseBody> httpPut(@Url String fullUrl, @HeaderMap Map<String, String> headers);    @FormUrlEncoded    @PUT    Flowable<ResponseBody> httpPut(@Url String fullUrl, @FieldMap Map<String, String> fields, @HeaderMap Map<String, String> headers);}