package cn.test.demo.presenter;import cn.test.demo.contract.WelcomeContract;import cn.test.demo.base.mvp.BasePresenter;import cn.test.demo.base.mvp.BaseSubscriber;import cn.test.demo.model.WelcomeModel;import io.reactivex.android.schedulers.AndroidSchedulers;import io.reactivex.schedulers.Schedulers;/** * Created by Sam on  2021-12-06  18:19 * Describe: */public class WelcomePresenter extends BasePresenter<WelcomeContract.Model, WelcomeContract.View> implements WelcomeContract.Presenter {    public WelcomePresenter() {        this.mModel = new WelcomeModel();    }    @Override    public void loadTestData(String cityId) {        mModel.loadTestData(cityId)                .subscribeOn(Schedulers.io())                .doOnSubscribe(subscription -> {                    mView.showLoading();                })                .observeOn(AndroidSchedulers.mainThread())                .doFinally(() -> {                    mView.hideLoading();                })                .compose(mView.bindToLife())                .subscribe(new BaseSubscriber<String>(mView) {                    @Override                    public void onNext(String msg) {                        mView.loadTestDataSuccess(msg);                    }                });    }}